Map organizeData() {
  Map grades = {};
  for (var item in cnbData['Primaria']) {
    if (grades.containsKey(item['grade'])) {
      var grade = grades[item['grade']];
      var subject = item['subjects'][0];
      grade['subjects'].add(subject);
    } else {
      var grade = {
        'grade': item['grade'],
        'subjects': [],
      };
      var subject = item['subjects'][0];
      grade['subjects'].add(subject);
      grades[item['grade']] = grade;
    }
  }
  return grades;
}

